CREATE TABLE USUARIO (ID VARCHAR(255) NOT NULL, EMAIL VARCHAR(255) UNIQUE, NOME VARCHAR(255), SENHA VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE CONFIGURACAO (ID VARCHAR(255) NOT NULL, ACADEMICOS VARCHAR(255), ACERVO VARCHAR(255), ACERVOFAUNA VARCHAR(255), ACERVOFLORA VARCHAR(255), AJUDEOMUSEU VARCHAR(255), BEMVINDO VARCHAR(255), EXTRAACADEMICOS VARCHAR(255), FUNDACAO VARCHAR(255), HISTORIA VARCHAR(255), HORARIOS VARCHAR(255), INSTITUCIONAL VARCHAR(255), LINKS VARCHAR(255), LOCALIZACAO VARCHAR(255), MAIS VARCHAR(255), MIDIA VARCHAR(255), MISSAOEOBJETIVOS VARCHAR(255), OMUSEU VARCHAR(255), OUTROSMUSEUS VARCHAR(255), PADRAO VARCHAR(255), PALESTRASEEVENTOS VARCHAR(255), VISITEOMUSEU VARCHAR(255), CONTENTTYPELOGO VARCHAR(255), EMAILPORTAL VARCHAR(255), FACEBOOK VARCHAR(255), FLICKR VARCHAR(255), LOGO BYTEA, MEMORIAVIRTUALLOGIN VARCHAR(255), MEMORIAVIRTUALSENHA VARCHAR(255), NOMEINSTITUICAO VARCHAR(255), NOMEINSTITUICAOMEMORIA VARCHAR(255), TAGEVENTS VARCHAR(255), TAGNEWS VARCHAR(255), TAGVOCESABIA VARCHAR(255), TWITTER VARCHAR(255), URLFREEGEOIP VARCHAR(255), URLINSTITUICAO VARCHAR(255), URLMEMORIA VARCHAR(255), URLPORTAL VARCHAR(255), URLWORDPRESS VARCHAR(255), YOUTUBE VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE SLIDE (ID VARCHAR(255) NOT NULL, CONTENT BYTEA, CONTENTTYPE VARCHAR(255), LINK VARCHAR(255), TEXTO VARCHAR(255), TITULO VARCHAR(255), CONFIGURACAO_ID VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE ACESSO (ID VARCHAR(255) NOT NULL, ACESSDATE DATE, AREACODE VARCHAR(255), CITY VARCHAR(255), COUNTRYCODE VARCHAR(255), COUNTRYNAME VARCHAR(255), IP VARCHAR(255), LATITUDE VARCHAR(255), LONGITUDE VARCHAR(255), METROCODE VARCHAR(255), REGIONCODE VARCHAR(255), REGIONNAME VARCHAR(255), ZIPCODE VARCHAR(255), PRIMARY KEY (ID));
ALTER TABLE SLIDE ADD CONSTRAINT FK_SLIDE_CONFIGURACAO_ID FOREIGN KEY (CONFIGURACAO_ID) REFERENCES CONFIGURACAO (ID);
CREATE SEQUENCE SLIDE_SEQ START WITH 1;
CREATE SEQUENCE USUARIO_SEQ START WITH 1;
CREATE SEQUENCE ACESSO_SEQ START WITH 1;